example.code:
private LazyValue<Dictionary<string, int>> _lookup = new(() => BuildExpensiveLookup());

public Dictionary<string, int> GetLookup()
{
    return _lookup.GetValue(); // Thread-safe, initialized exactly once
}

private LazyValue<MyCollection, Dictionary<string, int>> _lookup = new(BuildLookupTable);

public Dictionary<string, int> GetLookup(MyCollection collection)
{
    return _lookup.GetValue(collection); // Thread-safe, initialized exactly once
}

private static Dictionary<string, int> BuildLookupTable(MyCollection collection)
{
    // Static method avoids closure allocation
    return collection.Items.ToDictionary(x => x.Key, x => x.Value);
}

remarks.code:
// Preferred: static method avoids closure
var lazy = new LazyValue<MyData, ProcessedData>(ProcessData);

// Avoid: lambda creates closure allocation
var lazy = new LazyValue<MyData, ProcessedData>(data => ExpensiveOperation(data, someField));

